SRC=""
KEY="2718281828459045235360287471352631415926535897932384626433832795"
IV="0102"
# https://serverfault.com/a/193967
writehex()
{
    local i
    while [ "$1" ]; do
        for ((i=0; i<${#1}; i+=2))
        do
            printf "\x${1:i:2}";
        done;
        shift;
    done
}
writehex $SRC > /tmp/test;
# https://botan.randombit.net/handbook/cli.html
botan cipher --cipher=AES-128/XTS --key=$KEY \
    --nonce=$IV /tmp/test > /tmp/test_res
# https://stackoverflow.com/a/15554717
hexdump -e '16/1 "%02x"' /tmp/test_res; echo